import React, { Component } from 'react';
import Rows from "./Rows";

const countryToCode = {'Cameroon': '237',
                       'Ethiopia': '251',
                        'Morocco': '212',
                        'Mozambique': '258',
                        'Uganda': '256'
};

const codeToCountry =  {'237': 'Cameroon',
                        '251': 'Ethiopia',
                        '212': 'Morocco',
                        '258': 'Mozambique',
                        '256': 'Uganda'
};

const codeToRegex = {'237': /\(237\)\ ?[2368]\d{7,8}$,/,
                     '251': /\(251\)\ ?[1-59]\d{8}$/,
                     '212': /\(212\)\ ?[5-9]\d{8}$/,
                     '258': /\(258\)\ ?[28]\d{7,8}$/,
                     '256': /\(256\)\ ?\d{9}$/
};

class DataLister extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataReady: false,
            data: []
        };
    }

    fetchData = () => {

        fetch('http://localhost:8080/getData')
            .then((res) => res.json())
            .then((res) => this.setState({
                data: res,
                dataReady: true
            }));
    };

    componentDidMount() {
        this.fetchData();
    }

    render() {

        if (!this.state.dataReady)
        {
            return <div />
        }

        /* Filling the cards array with cards */
        const rows: JSX.Element[] = [];
        rows.push(<tr>
                    <th>Name</th>
                    <th>Phone</th>
                    <th>Country</th>
                    <th>Code</th>
                    {/*<th>State</th>*/}
                </tr>);

        let name, phoneNumber, code, country;

        for (let i = 0; i < this.state.data.length; i++)
        {


            name = this.state.data[i].name;
            [ code, phoneNumber ] = this.state.data[i].phoneNumber.split(' ');
            code = code.replace('(', '').replace(')', '');
            country = codeToCountry[code];
            // console.log(name);
            // console.log(phoneNumber);
            rows.push(<Rows customerName={name} phone={phoneNumber} country={country} code={code}/>)
        }

        return (
            <div style={{overflow:"auto;"}} >
                <table>

                    {rows}

                </table>
            </div>

        );
    }

}

export default DataLister;
