import React, { Component } from 'react';
import Rows from './Rows';
import { codeToCountry, codeToRegex } from './../Resources';

class DataLister extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataReady: false,
            data: [],
            originalData: [],
            filterKey: '',
            selection: ''
        };
    }

    isValid = (phoneNumber, code) => {
        return RegExp(codeToRegex[code]).test(phoneNumber) ? 'valid' : 'not valid';
    };

    fetchData = () => {

        fetch('http://localhost:8080/getData')
            .then((res) => res.json())
            .then((res) => this.setState({
                data: res.reduce((customers, currCustomer) => {

                    let [ code, phoneNumber ] = currCustomer.phoneNumber.split(' ');
                    code = code.replace('(', '').replace(')', '');

                    let customer = {
                        name: currCustomer.name,
                        phoneNumber: phoneNumber,
                        code: code,
                        country: codeToCountry[code],
                        validation: this.isValid(currCustomer.phoneNumber, code)
                    };

                    customers.push(customer);

                    return customers;
                }, []),

                dataReady: true
            }))
            .then(() => {
                this.setState({ originalData: [...this.state.data] } )
            });
    };

    filterData = (e) => {

        e.preventDefault();

        /* If there is not selection filter */
        if (this.state.selection === '')
        {
            return;
        }

        this.setState({
            /* Filter the data according to the selection and the search key */
            data: this.state.originalData.filter((customer) => {
                return customer[this.state.selection].startsWith(this.state.filterKey) === true;
            })
        });
    };

    handleChange = (e) => {

        if (e.target.id === 'filterSelection')
        {
            this.setState({ selection: e.target.value })
        }
        else if (e.target.id === 'searchKey')
        {
            this.setState({ filterKey : e.target.value })
        }
    };

    reset = () => {
        /* reset the data */
        this.setState({
            data: [...this.state.originalData]
        })
    };

    componentDidMount() {
        this.fetchData();
    }

    render() {

        /* If the data is not loaded */
        if (!this.state.dataReady)
        {
            return <div />
        }

        /* Filling the table with rows */
        const rows: JSX.Element[] = [];
        rows.push(<tr>
                        <th style={{textAlign: "center"}}>Name</th>
                        <th style={{textAlign: "center"}}>Country</th>
                        <th style={{textAlign: "center"}}>Code</th>
                        <th style={{textAlign: "center"}}>Phone</th>
                        <th style={{textAlign: "center"}}>State</th>
                  </tr>);

        let customer;

        for (let i = 0; i < this.state.data.length; i++)
        {
            customer = this.state.data[i];

            rows.push(<Rows customerName={customer.name} phone={customer.phoneNumber} country={customer.country} code={'+' + customer.code} state={customer.validation}/>)
        }

        return (

            <div style={{overflow:"auto"}} >

                <label style={{paddingLeft: 900, fontSize: 30}}>Filter</label>

                <select id={"filterSelection"} onChange={ this.handleChange }>
                    <option value="" selected disabled hidden>Select</option>
                    <option value={"country"}>Country</option>
                    <option value={"validation"}>State</option>
                </select>

                <input type="text" id="searchKey" name="key" onChange={ this.handleChange }/>
                <input type={"submit"} id={"filter"} value={"Filter"} onClick={ this.filterData }/>
                <input type={"submit"} id={"reset"}  value={"Reset"} onClick={ this.reset }/>

                <table>
                    {rows}
                </table>

            </div>

        );
    }
}

export default DataLister;
