import React, { Component } from 'react';
import Rows from "./Rows";

const countryToCode = {'Cameroon': '237',
                       'Ethiopia': '251',
                        'Morocco': '212',
                        'Mozambique': '258',
                        'Uganda': '256'
};

const codeToCountry =  {'237': 'Cameroon',
                        '251': 'Ethiopia',
                        '212': 'Morocco',
                        '258': 'Mozambique',
                        '256': 'Uganda'
};

const codeToRegex = {'237': "\\(237\\)\\ ?[2368]\\d{7,8}$",
                     '251': "\\(251\\)\\ ?[1-59]\\d{8}$",
                     '212': "\\(212\\)\\ ?[5-9]\\d{8}$",
                     '258': "\\(258\\)\\ ?[28]\\d{7,8}$",
                     '256': "\\(256\\)\\ ?\\d{9}$"
};

class DataLister extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataReady: false,
            data: []
        };
    }

    isValid = (phoneNumber, code) => {
        return RegExp(codeToRegex[code]).test(phoneNumber) ? 'valid' : 'not valid';
    };

    fetchData = () => {

        fetch('http://localhost:8080/getData')
            .then((res) => res.json())
            .then((res) => this.setState({
                data: res.reduce((customers, currCustomer) => {
                    console.log(currCustomer.name);
                    let [ code, phoneNumber ] = currCustomer.phoneNumber.split(' ');
                    code = code.replace('(', '').replace(')', '');
                    let customer = {
                        name: currCustomer.name,
                        phoneNumber: phoneNumber,
                        code: code,
                        country: codeToCountry[code],
                        validation: this.isValid(currCustomer.phoneNumber, code)
                    };
                    customers.push(customer);
                    return customers;
                }, []),
                dataReady: true
            }));
    };

    componentDidMount() {
        this.fetchData();
    }

    render() {

        if (!this.state.dataReady)
        {
            return <div />
        }

        /* Filling the cards array with cards */
        const rows: JSX.Element[] = [];
        rows.push(<tr>
                    <th style={{textAlign: "center"}}>Name</th>
                    <th style={{textAlign: "center"}}>Country</th>
                    <th style={{textAlign: "center"}}>Code</th>
                    <th style={{textAlign: "center"}}>Phone</th>
                    <th style={{textAlign: "center"}}>State</th>
                </tr>);

        let customer;

        for (let i = 0; i < this.state.data.length; i++)
        {
            customer = this.state.data[i]

            rows.push(<Rows customerName={customer.name} phone={customer.phoneNumber} country={customer.country} code={'+' + customer.code} state={customer.validation}/>)
        }

        return (
            <div style={{overflow:"auto"}} >
                <table>

                    {rows}

                </table>
            </div>

        );
    }

}

export default DataLister;
