import React, { Component } from 'react';
import Rows from "./Rows";

class DataLister extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataReady: false,
            data: []
        };
    }

    fetchData = () => {

        fetch('http://localhost:8080/getData')
            .then((res) => res.json())
            .then((res) => this.setState({
                data: res,
                dataReady: true
            }));
    };

    componentDidMount() {
        this.fetchData();
    }

    render() {

        if (!this.state.dataReady)
        {
            return <div />
        }

        /* Filling the cards array with cards */
        const rows: JSX.Element[] = [];
        rows.push(<tr>
                    <th>Name</th>
                    <th>Phone</th>
                </tr>);
        let name, phoneNumber;
        for (let i = 0; i < 10; i++)
        {


            name = this.state.data[i].name;
            phoneNumber = this.state.data[i].phoneNumber;

            console.log(name);
            console.log(phoneNumber);
            rows.push(<Rows customerName={name} phone={phoneNumber}/>)
        }

        return (
            <table>

                {rows}

            </table>
        );
    }

}

export default DataLister;
